import React from "react";
import ReactDom from "react-dom";
import './index.css';

// ==vars=



const books = [
  {
    id: 1,
    img: "https://images-eu.ssl-images-amazon.com/images/I/61Vj89VXrxL._AC_SX184_.jpg",
    title: "101 Tales from Indian Mythology: Illustrated Stories for Children",
    author: "Om Books Editorial Team",
  },
  {
    id: 2,
    img: "https://images-eu.ssl-images-amazon.com/images/I/51S3Dg2WCBL._AC_SX184_.jpg",
    title: "Write Me A Love Story",
    author: "Ravinder Singh",
  },
  {
    id: 3,
    img: "https://images-eu.ssl-images-amazon.com/images/I/41FOswdIdGS._AC_SX184_.jpg",
    title: "Mortuary Tales",
    author: "Kashif Mashaikh",
  },
];

function Booklist(){
  return <section className="booklist">{books.map((book)=>{

    return <div>
      <Book key={book.id} {...book} ></Book>
    </div>
  })}</section>;
}

const clickHandler = () => {
  alert
}

const Book = (props) => {
  const { img, title, author } = props;   
  console.log(props);
  return (
    <section className="booklist">
      <img src={img} alt=""/>
      <h3>{title}</h3>
      <p>{author}</p>
      <button type="button" onClick={clickHandler}>Reference</button>
    </section>
  );
};






ReactDom.render(<Booklist />, document.getElementById("root"));
